        protected static readonly String[]  Split1  = { "\r\n" };
        protected static readonly String[]  Split2  = { ": " };
        protected static readonly String[]  Split3  = { " " };
        protected static readonly Char[]    Split4  = { ',' };
        protected static readonly Char[]    Split5  = { '|' };

