                                             #region Parse JSON

                                             if (!Request.TryParseJObjectRequestBody(out JObject JSONObj, out HTTPResponse))
                                                 return HTTPResponse;

                                             var ErrorResponse    = "";
                                             var CurrentPassword  = JSONObj.GetString("currentPassword");
