                                                                Date                        = DateTime.UtcNow,
                                                                AccessControlAllowOrigin    = "*",
                                                                AccessControlAllowMethods   = "SET",
                                                                AccessControlAllowHeaders   = "Content-Type, Accept, Authorization",
                                                                Connection                  = "close"
                                                            }.AsImmutable;

                                             }

                                             else
