        public ECPrivateKeyParameters ParsePrivateKeyHEX(X9ECParameters  ECParameters,
                                                         String          PrivateKeyHEX)

            => ParsePrivateKeyHEX(new ECDomainParameters(ECParameters.Curve,
                                                         ECParameters.G,
                                                         ECParameters.N,
                                                         ECParameters.H,
                                                         ECParameters.GetSeed()),
