        /// <summary>
        /// An enumeration of all users.
        /// </summary>
        protected internal readonly Dictionary<User_Id, User> _Users;

        /// <summary>
        /// An enumeration of all users.
        /// </summary>
        public IEnumerable<User> Users
        {
            get
            {

                var lockTaken = UsersSemaphore.Wait(SemaphoreSlimTimeout);

                try
                {
                    return lockTaken
                               ? _Users.Values.ToArray()
                               : new User[0];
                }
                finally
                {
                    try
                    {
                        if (lockTaken)
                            UsersSemaphore.Release();
                    }
                    catch
                    { }
                }

            }
        }

        #endregion


