        /// <summary>
        /// An enumeration of all organizations.
        /// </summary>
        protected internal readonly Dictionary<Organization_Id, Organization> _Organizations;

        /// <summary>
        /// An enumeration of all organizations.
        /// </summary>
        public IEnumerable<Organization> Organizations
        {
            get
            {

                var lockTaken = OrganizationsSemaphore.Wait(SemaphoreSlimTimeout);

                try
                {
                    return lockTaken
                               ? _Organizations.Values.ToArray()
                               : new Organization[0];
                }
                finally
                {
                    try
                    {
                        if (lockTaken)
                            OrganizationsSemaphore.Release();
                    }
                    catch
                    { }
                }

            }
        }

