        #region (protected internal) WriteToDatabaseFileAndNotify(Organization, MessageType,  OldOrganization = null, CurrentUserId = null)

        /// <summary>
        /// Write the given organization to the database and send out notifications.
        /// </summary>
        /// <param name="Organization">The organization.</param>
        /// <param name="MessageType">The organization notification.</param>
        /// <param name="OldOrganization">The old/updated organization.</param>
        /// <param name="EventTrackingId">An optional unique event tracking identification for correlating this request with other events.</param>
        /// <param name="CurrentUserId">An optional user identification initiating this command/request.</param>
        protected internal async Task WriteToDatabaseFileAndNotify(Organization             Organization,
                                                                   NotificationMessageType  MessageType,
                                                                   Organization             OldOrganization   = null,
                                                                   EventTracking_Id         EventTrackingId   = null,
                                                                   User_Id?                 CurrentUserId     = null)
        {

            if (Organization is null)
                throw new ArgumentNullException(nameof(Organization),  "The given organization must not be null or empty!");

            if (MessageType.IsNullOrEmpty)
                throw new ArgumentNullException(nameof(MessageType),   "The given message type must not be null or empty!");


            var eventTrackingId = EventTrackingId ?? EventTracking_Id.New;

            await WriteToDatabaseFile(MessageType,
                                      Organization.ToJSON(false, true),
                                      eventTrackingId,
                                      CurrentUserId);

            await SendNotifications(Organization,
                                    MessageType,
                                    OldOrganization,
                                    eventTrackingId,
                                    CurrentUserId);

        }

        #endregion

        #region (protected internal) SendNotifications           (Organization, MessageTypes, OldOrganization = null, CurrentUserId = null)

        /// <summary>
        /// Send organization notifications.
        /// </summary>
        /// <param name="Organization">The organization.</param>
        /// <param name="MessageType">The organization notification.</param>
        /// <param name="OldOrganization">The old/updated organization.</param>
        /// <param name="EventTrackingId">An optional unique event tracking identification for correlating this request with other events.</param>
        /// <param name="CurrentUserId">The invoking organization identification</param>
        protected internal async virtual Task SendNotifications(Organization             Organization,
                                                                NotificationMessageType  MessageType,
                                                                Organization             OldOrganization   = null,
                                                                EventTracking_Id         EventTrackingId   = null,
                                                                User_Id?                 CurrentUserId     = null)
        {

            if (Organization is null)
                throw new ArgumentNullException(nameof(Organization),  "The given organization must not be null or empty!");

            if (MessageType.IsNullOrEmpty)
                throw new ArgumentNullException(nameof(MessageType),   "The given message type must not be null or empty!");


            await SendNotifications(Organization,
                                    new NotificationMessageType[] { MessageType },
                                    OldOrganization,
                                    EventTrackingId,
                                    CurrentUserId);

        }


        /// <summary>
        /// Send organization notifications.
        /// </summary>
        /// <param name="Organization">The organization.</param>
        /// <param name="MessageTypes">The organization notifications.</param>
        /// <param name="OldOrganization">The old/updated organization.</param>
        /// <param name="EventTrackingId">An optional unique event tracking identification for correlating this request with other events.</param>
        /// <param name="CurrentUserId">The invoking organization identification</param>
        protected internal async virtual Task SendNotifications(Organization                          Organization,
                                                                IEnumerable<NotificationMessageType>  MessageTypes,
                                                                Organization                          OldOrganization   = null,
                                                                EventTracking_Id                      EventTrackingId   = null,
                                                                User_Id?                              CurrentUserId     = null)
        {

            if (Organization is null)
                throw new ArgumentNullException(nameof(Organization),  "The given organization must not be null or empty!");

            var messageTypesHash = new HashSet<NotificationMessageType>(MessageTypes.Where(messageType => !messageType.IsNullOrEmpty));

            if (messageTypesHash.IsNullOrEmpty())
                throw new ArgumentNullException(nameof(MessageTypes),  "The given enumeration of message types must not be null or empty!");

            if (messageTypesHash.Contains(addOrganizationIfNotExists_MessageType))
                messageTypesHash.Add(addOrganization_MessageType);

            if (messageTypesHash.Contains(addOrUpdateOrganization_MessageType))
                messageTypesHash.Add(OldOrganization == null
                                       ? addOrganization_MessageType
                                       : updateOrganization_MessageType);

            var messageTypes = messageTypesHash.ToArray();


            if (!DisableNotifications)
            {

                #region Telegram Notifications

                if (TelegramStore != null)
                {
                    try
                    {

                        var AllTelegramNotifications  = this.GetTelegramNotifications(Organization, messageTypes).
                                                             ToHashSet();

                        if (DevMachines.Contains(Environment.MachineName))
                        {
                            AllTelegramNotifications.Clear();
                        }

                        if (AllTelegramNotifications.Count > 0)
                        {

                            if (messageTypes.Contains(addOrganization_MessageType))
                            {
                                await TelegramStore.SendTelegrams(String.Concat("Organization '", Organization.Name.FirstText(), "' was successfully created. ",
                                                                                "https://", ExternalDNSName, BasePath, "/organizations/", Organization.Id),
                                                                  AllTelegramNotifications.Select(TelegramNotification => TelegramNotification.Username));
                            }

                            if (messageTypes.Contains(updateOrganization_MessageType))
                            {
                                await TelegramStore.SendTelegrams(String.Concat("Organization '", Organization.Name.FirstText(), "' information had been successfully updated. ",
                                                                                "https://", ExternalDNSName, BasePath, "/organizations/", Organization.Id),
                                                                                // + {Updated information}
                                                                  AllTelegramNotifications.Select(TelegramNotification => TelegramNotification.Username));
                            }

                            if (messageTypes.Contains(deleteOrganization_MessageType))
                            {
                                await TelegramStore.SendTelegrams(String.Concat("Organization '", Organization.Name.FirstText(), "' has been removed. ",
                                                                                "If you haven't approved this request, please contact support: support@cardi-link.com"),
                                                                  AllTelegramNotifications.Select(TelegramNotification => TelegramNotification.Username));
                            }

                        }

                    }
                    catch (Exception e)
                    {
                        DebugX.Log(e.Message);
                    }
                }

                #endregion

                #region SMS Notifications

                try
                {

                    var AllSMSNotifications  = this.GetSMSNotifications(Organization, messageTypes).
                                                    ToHashSet();

                    if (DevMachines.Contains(Environment.MachineName))
                    {
                        AllSMSNotifications.Clear();
                    }

                    if (AllSMSNotifications.Count > 0)
                    {

                        if (messageTypes.Contains(addOrganization_MessageType))
                        {
                            SendSMS(String.Concat("Organization '", Organization.Name.FirstText(), "' was successfully created. ",
                                                  "https://", ExternalDNSName, BasePath, "/organizations/", Organization.Id),
                                    AllSMSNotifications.Select(smsPhoneNumber => smsPhoneNumber.PhoneNumber.ToString()).ToArray(),
                                    SMSSenderName);
                        }

                        if (messageTypes.Contains(updateOrganization_MessageType))
                        {
                            SendSMS(String.Concat("Organization '", Organization.Name.FirstText(), "' information had been successfully updated. ",
                                                  "https://", ExternalDNSName, BasePath, "/organizations/", Organization.Id),
                                                  // + {Updated information}
                                    AllSMSNotifications.Select(smsPhoneNumber => smsPhoneNumber.PhoneNumber.ToString()).ToArray(),
                                    SMSSenderName);
                        }

                        if (messageTypes.Contains(deleteOrganization_MessageType))
                        {
                            SendSMS(String.Concat("Organization '", Organization.Name.FirstText(), "' has been removed."),
                                    AllSMSNotifications.Select(smsPhoneNumber => smsPhoneNumber.PhoneNumber.ToString()).ToArray(),
                                    SMSSenderName);
                        }

                    }

                }
                catch (Exception e)
                {
                    DebugX.Log(e.Message);
                }

                #endregion

                #region HTTPS Notifications

                try
                {

                    var AllHTTPSNotifications  = this.GetHTTPSNotifications(Organization, messageTypes).
                                                      ToHashSet();

                    if (DevMachines.Contains(Environment.MachineName))
                        AllHTTPSNotifications.Clear();

                    if (AllHTTPSNotifications.Count > 0)
                    {

                        if (messageTypes.Contains(addOrganization_MessageType))
                        {

                            await SendHTTPSNotifications(AllHTTPSNotifications,
                                                         new JObject(
                                                             new JProperty("organizationCreated",
                                                                 Organization.ToJSON()
                                                             ),
                                                             new JProperty("timestamp", DateTime.UtcNow.ToIso8601())
                                                         ));

                        }

                        if (messageTypes.Contains(updateOrganization_MessageType))
                        {

                            await SendHTTPSNotifications(AllHTTPSNotifications,
                                                         new JObject(
                                                             new JProperty("organizationUpdated",
                                                                 Organization.ToJSON()
                                                             ),
                                                             new JProperty("timestamp", DateTime.UtcNow.ToIso8601())
                                                         ));

                        }

                        if (messageTypes.Contains(deleteOrganization_MessageType))
                        {

                            await SendHTTPSNotifications(AllHTTPSNotifications,
                                                         new JObject(
                                                             new JProperty("organizationRemoved",
                                                                 Organization.ToJSON()
                                                             ),
                                                             new JProperty("timestamp", DateTime.UtcNow.ToIso8601())
                                                         ));

                        }

                    }

                }
                catch (Exception e)
                {
                    DebugX.Log(e.Message);
                }

                #endregion

                #region EMailNotifications

                if (APISMTPClient != null)
                {
                    try
                    {

                        var AllEMailNotifications  = this.GetEMailNotifications(Organization, messageTypes).
                                                          ToHashSet();

                        if (DevMachines.Contains(Environment.MachineName))
                        {
                            AllEMailNotifications.Clear();
                        }

                        if (AllEMailNotifications.Count > 0)
                        {

                            if (messageTypes.Contains(addOrganization_MessageType))
                            {

                                await APISMTPClient.Send(
                                         new HTMLEMailBuilder() {

                                             From           = Robot.EMail,
                                             To             = EMailAddressListBuilder.Create(EMailAddressList.Create(AllEMailNotifications.Select(emailnotification => emailnotification.EMailAddress))),
                                             Passphrase     = APIPassphrase,
                                             Subject        = String.Concat("Organization '", Organization.Name.FirstText(), "' was successfully created."),

                                             HTMLText       = String.Concat(HTMLEMailHeader(ExternalDNSName, BasePath, EMailType.Notification),
                                                                            "Organization <a href=\"https://", ExternalDNSName, BasePath, "/organizations/", Organization.Id, "\">", Organization.Name.FirstText(), "</a> was successfully created.",
                                                                            HTMLEMailFooter(ExternalDNSName, BasePath, EMailType.Notification)),

                                             PlainText      = String.Concat(TextEMailHeader(ExternalDNSName, BasePath, EMailType.Notification),
                                                                            "Organization '", Organization.Name.FirstText(), "' was successfully created.\r\n",
                                                                            "https://", ExternalDNSName, BasePath, "/organizations/", Organization.Id, "\r\r\r\r",
                                                                            TextEMailFooter(ExternalDNSName, BasePath, EMailType.Notification)),

                                             SecurityLevel  = EMailSecurity.sign

                                         });

                            }

                            if (messageTypes.Contains(updateOrganization_MessageType))
                            {
                                await APISMTPClient.Send(
                                         new HTMLEMailBuilder() {

                                             From           = Robot.EMail,
                                             To             = EMailAddressListBuilder.Create(EMailAddressList.Create(AllEMailNotifications.Select(emailnotification => emailnotification.EMailAddress))),
                                             Passphrase     = APIPassphrase,
                                             Subject        = String.Concat("Organization '", Organization.Name.FirstText(), "' information had been successfully updated."),

                                             HTMLText       = String.Concat(HTMLEMailHeader(ExternalDNSName, BasePath, EMailType.Notification),
                                                                            "Organization <a href=\"https://", ExternalDNSName, BasePath, "/organizations/", Organization.Id, "\">", Organization.Name.FirstText(), "</a> information had been successfully updated.",
                                                                            // + {Updated information}",
                                                                            HTMLEMailFooter(ExternalDNSName, BasePath, EMailType.Notification)),

                                             PlainText      = String.Concat(TextEMailHeader(ExternalDNSName, BasePath, EMailType.Notification),
                                                                            "Organization '", Organization.Name.FirstText(), "' information had been successfully updated.\r\n",
                                                                            "https://", ExternalDNSName, BasePath, "/organizations/", Organization.Id, "\r\r\r\r",
                                                                            // + {Updated information}",
                                                                            TextEMailFooter(ExternalDNSName, BasePath, EMailType.Notification)),

                                             SecurityLevel  = EMailSecurity.sign

                                         });
                            }

                            if (messageTypes.Contains(deleteOrganization_MessageType))
                            {
                                await APISMTPClient.Send(
                                         new HTMLEMailBuilder() {

                                             From           = Robot.EMail,
                                             To             = EMailAddressListBuilder.Create(EMailAddressList.Create(AllEMailNotifications.Select(emailnotification => emailnotification.EMailAddress))),
                                             Passphrase     = APIPassphrase,
                                             Subject        = String.Concat("Organization '", Organization.Name.FirstText(), "' has been removed."),

                                             HTMLText       = String.Concat(HTMLEMailHeader(ExternalDNSName, BasePath, EMailType.Notification),
                                                                            "Organization <a href=\"https://", ExternalDNSName, BasePath, "/organizations/", Organization.Id, "\">", Organization.Name.FirstText(), "</a> has been removed.<br />",
                                                                            HTMLEMailFooter(ExternalDNSName, BasePath, EMailType.Notification)),

                                             PlainText      = String.Concat(TextEMailHeader(ExternalDNSName, BasePath, EMailType.Notification),
                                                                            "Organization '", Organization.Name.FirstText(), "' has been removed.\r\n",
                                                                            TextEMailFooter(ExternalDNSName, BasePath, EMailType.Notification)),

                                             SecurityLevel  = EMailSecurity.sign

                                         });
                            }

                        }

                    }
                    catch (Exception e)
                    {
                        DebugX.Log(e.Message);
                    }
                }

                #endregion

            }

        }

        #endregion

        #region (protected internal) GetOrganizationSerializator (Request, Organization)

        protected internal OrganizationToJSONDelegate GetOrganizationSerializator(HTTPRequest  Request,
                                                                                  User         User)
        {

            switch (User?.Id.ToString())
            {

                default:
                    return (organization,
                            embedded,
                            expandMembers,
                            expandParents,
                            expandSubOrganizations,
                            expandTags,
                            includeCryptoHash)

                            => organization.ToJSON(embedded,
                                                   expandMembers,
                                                   expandParents,
                                                   expandSubOrganizations,
                                                   expandTags,
                                                   includeCryptoHash);

            }

        }

