            //            // Fail early!
            //            if (currentUsers.Contains(Member))
            //                return false;

            //        }

            //        else if (currentUsers.Contains(Member))
            //        {

            //            // Astronaut and member are on the same level, e.g. both admin of the same organization!
            //            if (currentUsers.Contains(Astronaut))
