            }
            catch (Exception e)
            {

                DebugX.LogException(e);

                return AddUserToOrganizationResult.Failed(User,
                                                          EdgeLabel,
                                                          Organization,
                                                          eventTrackingId,
                                                          e);

            }
            finally
            {
                try
                {
                    if (usersLockTaken)
                        OrganizationsSemaphore.Release();
                }
                catch
                { }

                try
                {
                    if (organizationsLockTaken)
                        UsersSemaphore.Release();
                }
                catch
                { }
            }

        }

